definition.script:
def label = "build-customize-${UUID.randomUUID().toString()}"
podTemplate(label: label,  containers: [
     containerTemplate(
            name: 'dind',
            image: "${dockerRepoHost}/commerce/deployslave:latest",
            ttyEnabled: true,
            privileged: true,
            command: 'cat',
            alwaysPullImage: true
        ),
    containerTemplate(
            name: 'jnlp',
            image: "${dockerRepoHost}/commerce/jnlp-slave:alpine",
            args: '${computer.jnlpmac} ${computer.name}',
            command: ''

        )
  ]
  ) 
{
    node (label) {

        container('dind') {
            def build_info="""
			{
				"group": "${Tenant}",
				"env": "${EnvName}",
				"envType": "${EnvType}",
				"dockerRepo": "${dockerRepoHost}",
				"bundleRepo": "${bundleRepo}",
				"images": [
   				 ${BuildInfo}
 				]

			}
			"""
            stage('Build WC V9 Customization Docker Image') {
                sh("dockerd --insecure-registry=${dockerRepoHost} &")
                sh("sleep 4")
                sh("echo y | docker login ${dockerRepoHost} -u ${dockerRepoUser} -p ${dockerRepoPwd}")
                sh("python3.6 /commerce-devops-utilities/utilities/dockerbuild/buildCusDocker.py -vu $vault_url -vt $vault_token -ru $dockerRepoUser -rp $dockerRepoPwd -fpl $ForcePull_Image -fph $ForcePush_Image -i '${build_info}' -rl commerce -ns $Namespace")
            }
        }
    }
}
