definition.script:
pipeline {
    agent none
    stages {
        stage('Create or Update Key-Value on Valut ') {
            agent { label 'master' }
            steps {
                sh "python3.6 /commerce-devops-utilities/utilities/vault/vault_keyvalue.py insertkv -vault_url ${vault_url} -vault_token ${vault_token} -tenant ${Tenant} -env ${EnvName} -keyvalues '${ConfigureOnVault}' -separater '\n'"
            }
        }
    }
}
